import React from "react";
import useFormValidation from "./Custom Hook/useFormValidation";
import './App.css';

const Form = () => {
  const {
    name,
    setName,
    email,
    setEmail,
    age,
    setAge,
    guestName,
    setGuestName,
    isAttendingWithGuest,
    setIsAttendingWithGuest,
    errors,
    validateForm,
  } = useFormValidation();

  const handleSubmit = (e) => {
    e.preventDefault();
    const isValid = validateForm();
    if (isValid) {
      // Display the summary of entered data
      alert(`Name: ${name}\nEmail: ${email}\nAge: ${age}\nAttending with Guest: ${isAttendingWithGuest ? "Yes" : "No"}\nGuest Name: ${guestName}`);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        {errors.name && <span className="error">{errors.name}</span>}
      </label>
      <label>
        Email:
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        {errors.email && <span className="error">{errors.email}</span>}
      </label>
      <label>
        Age:
        <input
          type="number"
          value={age}
          onChange={(e) => setAge(e.target.value)}
        />
        {errors.age && <span className="error">{errors.age}</span>}
      </label>
      <label>
        Are you attending with a guest?
        <input
          type="checkbox"
          checked={isAttendingWithGuest}
          onChange={(e) => setIsAttendingWithGuest(e.target.checked)}
        />
      </label>
      {isAttendingWithGuest && (
        <label>
          Guest Name:
          <input
            type="text"
            value={guestName}
            onChange={(e) => setGuestName(e.target.value)}
          />
          {errors.guestName && <span className="error">{errors.guestName}</span>}
        </label>
      )}
      <button type="submit">Submit</button>
    </form>
  );
};

export default Form;
