import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import useFormValidation from "./Custom Hook/useFormValidation";

const Form = () => {
  const {
    name,
    setName,
    email,
    setEmail,
    age,
    setAge,
    guestName,
    setGuestName,
    errors,
    validateForm,
  } = useFormValidation();

  const [isAttendingWithGuest, setIsAttendingWithGuest] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    const isValid = validateForm();
    if (isValid) {
      navigate("/summary", {
        state: {
          name,
          email,
          age,
          isAttendingWithGuest,
          guestName,
        },
      });
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="name">Name:</label>
      <input
        type="text"
        id="name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        autoComplete="off"
      />
      {errors.name && <span className="error">{errors.name}</span>}
      <br />

      <label htmlFor="email">Email:</label>
      <input
        type="email"
        id="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      {errors.email && <span className="error">{errors.email}</span>}
      <br />

      <label htmlFor="age">Age:</label>
      <input
        type="number"
        id="age"
        value={age}
        onChange={(e) => setAge(e.target.value)}
      />
      {errors.age && <span className="error">{errors.age}</span>}
      <br />

      <label>Are you attending with a guest?</label>
      <br />
      <label htmlFor="attendingYes">
        <input
          type="radio"
          id="attendingYes"
          name="attendingGuest"
          value="yes"
          checked={isAttendingWithGuest}
          onChange={() => setIsAttendingWithGuest(true)}
        />
        Yes
      </label>
      <label htmlFor="attendingNo">
        <input
          type="radio"
          id="attendingNo"
          name="attendingGuest"
          value="no"
          checked={!isAttendingWithGuest}
          onChange={() => setIsAttendingWithGuest(false)}
        />
        No
      </label>
      <br />

      {isAttendingWithGuest && (
        <>
          <label htmlFor="guestName">Guest Name:</label>
          <input
            type="text"
            id="guestName"
            value={guestName}
            onChange={(e) => setGuestName(e.target.value)}
            autoComplete="off"
          />
          {errors.guestName && <span className="error">{errors.guestName}</span>}
          <br />
        </>
      )}

      <button type="submit">Submit</button>
    </form>
  );
};

export default Form;
