import { useState } from "react";

const useFormValidation = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [age, setAge] = useState("");
  const [guestName, setGuestName] = useState("");
  const [isAttendingWithGuest, setIsAttendingWithGuest] = useState(false);
  const [errors, setErrors] = useState({});

  const validateEmail = (email) => {
    // Email validation regex
    const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return re.test(email);
  };

  const validateForm = () => {
    const errors = {};

    if (!name.trim()) {
      errors.name = "Name is required";
    }

    if (!email.trim() || !validateEmail(email)) {
      errors.email = "Email is required and must be a valid email format";
    }

    if (!age.trim() || isNaN(age) || parseInt(age) <= 0) {
      errors.age = "Age is required and must be a number greater than 0";
    }

    if (isAttendingWithGuest && !guestName.trim()) {
      errors.guestName = "Guest Name is required";
    }

    setErrors(errors);

    return Object.keys(errors).length === 0;
  };

  return {
    name,
    setName,
    email,
    setEmail,
    age,
    setAge,
    guestName,
    setGuestName,
    isAttendingWithGuest,
    setIsAttendingWithGuest,
    errors,
    validateForm,
  };
};

export default useFormValidation;
