import React, { useState } from "react";
import useFormValidation from "./Custom Hook/useFormValidation";

const Form = () => {
  const {
    name,
    setName,
    email,
    setEmail,
    age,
    setAge,
    guestName,
    setGuestName,
    errors,
    validateForm,
  } = useFormValidation();

  const [isAttendingWithGuest, setIsAttendingWithGuest] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    const isValid = validateForm();
    if (isValid) {
      // Display the summary of entered data
      document.getElementById("summary").innerHTML = `
        <p>Name: ${name}</p>
        <p>Email: ${email}</p>
        <p>Age: ${age}</p>
        <p>Attending with Guest: ${isAttendingWithGuest ? "Yes" : "No"}</p>
        ${isAttendingWithGuest ? `<p>Guest Name: ${guestName}</p>` : ""}
      `;
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        {errors.name && <span className="error">{errors.name}</span>}
      </label>
      <label>
        Email:
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        {errors.email && <span className="error">{errors.email}</span>}
      </label>
      <label>
        Age:
        <input
          type="number"
          value={age}
          onChange={(e) => setAge(e.target.value)}
        />
        {errors.age && <span className="error">{errors.age}</span>}
      </label>
      <label>
        Are you attending with a guest?
        <div>
          <input
            type="radio"
            id="yes"
            name="guestAttendance"
            value="yes"
            checked={isAttendingWithGuest}
            onChange={() => setIsAttendingWithGuest(true)}
          />
          <label htmlFor="yes">Yes</label>
        </div>
        <div>
          <input
            type="radio"
            id="no"
            name="guestAttendance"
            value="no"
            checked={!isAttendingWithGuest}
            onChange={() => setIsAttendingWithGuest(false)}
          />
          <label htmlFor="no">No</label>
        </div>
      </label>
      {isAttendingWithGuest && (
        <label>
          Guest Name:
          <input
            type="text"
            value={guestName}
            onChange={(e) => setGuestName(e.target.value)}
          />
          {errors.guestName && <span className="error">{errors.guestName}</span>}
        </label>
      )}
      <button type="submit">Submit</button>
      <div id="summary"></div>
    </form>
  );
};

export default Form;
