import React from "react";

function App() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [age, setAge] = useState(0);
  const [isAttendingWithGuest, setIsAttendingWithGuest] = useState(false);
  const [guestName, setGuestName] = useState('');

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handleAgeChange = (event) => {
    setAge(parseInt(event.target.value));
  };

  const handleGuestAttendanceChange = (event) => {
    setIsAttendingWithGuest(event.target.value === 'yes');
  };

  const handleGuestNameChange = (event) => {
    setGuestName(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Form submitted!');
    console.log('Name:', name);
    console.log('Email:', email);
    console.log('Age:', age);
    console.log('Attending with Guest:', isAttendingWithGuest);
    console.log('Guest Name:', guestName);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="name">Name: </label>
      <input type="text" id="name" value={name} onChange={handleNameChange} required />
      <br />

      <label htmlFor="email">Email: </label>
      <input type="email" id="email" value={email} onChange={handleEmailChange} required />
      <br />

      <label htmlFor="age">Age: </label>
      <input type="number" id="age" value={age} onChange={handleAgeChange} required />
      <br />

      <label>Are you attending with a guest? </label>
      <br />
      <input
        type="radio"
        id="yes"
        name="guestAttendance"
        value="yes"
        checked={isAttendingWithGuest}
        onChange={handleGuestAttendanceChange}
      />
      <label htmlFor="yes">Yes</label>
      <br />
      <input
        type="radio"
        id="no"
        name="guestAttendance"
        value="no"
        checked={!isAttendingWithGuest}
        onChange={handleGuestAttendanceChange}
      />
      <label htmlFor="no">No</label>
      <br />

      {isAttendingWithGuest && (
        <div>
          <label htmlFor="guestName">Guest Name: </label>
          <input type="text" id="guestName" value={guestName} onChange={handleGuestNameChange} required />
          <br />
        </div>
      )}

      <button type="submit">Submit</button>
    </form>
  );
}

export default App;
